<style>
	.Main .confirmacion .wrap {
		margin-bottom: 50px;
		display: flex;
		align-items: center;
		text-align: center;
		justify-content: center;
	}

	.Main .confirmacion .button {
		font-family: Moanas;
		width: 70%;
		font-size: 12px;
		text-transform: uppercase;
		letter-spacing: 1.3px;
		font-weight: 700;
		color: var(--secondary-background);
		background: var(--mar2-background);
		background: linear-gradient(90deg, 
		var(--mar2-background) 50%, 
		var(--mar1-background) 100%);
		border: none;
		border-radius: 1000px;
		box-shadow: 0 0 60px var(--cuatry-background);
		transition: all 0.3s ease-in-out 0s;
		cursor: pointer;
		outline: none;
		position: relative;
		padding: 10px;
	}

	.Main .confirmacion button::before {
		content: '';
		border-radius: 1000px;
		min-width: calc(300px + 12px);
		min-height: calc(60px + 12px);
		/* border: 6px solid var(--primary-background) ;
		box-shadow: 0 0 60px var(--cuatry-background); */
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		opacity: 0;
		transition: all 0.3s ease-in-out 0s;
	}

	.Main .confirmacion .button:hover,
	.button:focus {
		color: var(--secondary-background);
		transform: translateY(-6px);
	}

	.Main .confirmacion button:hover::before,
	button:focus::before {
		opacity: 1;
	}

	.Main .confirmacion button::after {
		content: '';
		width: 30px;
		height: 30px;
		border-radius: 100%;
		border: 6px solid var(--primary-background)b;
		position: absolute;
		z-index: -1;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		animation: ring 1.5s infinite;
	}

	button:hover::after,
	button:focus::after {
		animation: none;
		display: none;
	}

	@keyframes ring {
		0% {
			width: 30px;
			height: 30px;
			opacity: 1;
		}
		100% {
			width: 300px;
			height: 300px;
			opacity: 0;
		}
	}
	* {
		width: 425px;
		max-width: 425px;
		margin: 0;
		padding: 0;
		color: var(--cuatry-background);
	}
